mapper.select:
SELECT DISTINCT T.TOUR_NO, T.GUIDE_NO, T.TITLE, T.DURATION, T.DESCRIPTION, T.PRICE, T.MAX_PEOPLE, T.TOUR_DATE, T.T_CREATED_AT, T.T_UPDATED_AT, T.DELETEYN, T.SI_NO, T.GU_NO, T.THEME_NO, M.*, I.*, R.*, G.*, TEMP1.* FROM TOURS T INNER JOIN THEME M ON T.THEME_NO = M.THEME_NO LEFT JOIN ( SELECT TOUR_NO, ROUND(AVG(RATING),1) AS RATING FROM REVIEWS GROUP BY TOUR_NO ) TEMP1 ON T.TOUR_NO = TEMP1.TOUR_NO LEFT JOIN IMG I ON T.TOUR_NO = I.TOUR_NO AND I.THUMBNAIL = 'Y' INNER JOIN ( SELECT DISTINCT SI_NO, SI_NAME FROM REGION ) R ON T.SI_NO = R.SI_NO INNER JOIN GUIDES G ON T.GUIDE_NO = G.GUIDE_NO WHERE T.DELETEYN = 'N'
AND T.SI_NO = #{siNo}
AND T.SI_NO IN (34, 37, 26, 38, 25, 22, 29, 24, 33, 36)
AND T.TITLE LIKE CONCAT('%', #{keyword}, '%')
AND (
DATE(T.TOUR_DATE) = #{item}
)
AND T.THEME_NO IN
#{item}
AND T.SI_NO IN
#{item}
AND (
G.LANGUAGE LIKE CONCAT('%', #{item}, '%')
)
ORDER BY T.TOUR_DATE LIMIT #{page}, #{pageSize}

mapper.update:
	UPDATE TOURS
    SET DELETEYN = #{deleteYN}
    WHERE TOUR_NO = #{tourNo}

mapper.insert:
INSERT INTO TOURS (
  GUIDE_NO, TITLE, DURATION, PRICE, MAX_PEOPLE,
  TOUR_DATE, T_CREATED_AT, T_UPDATED_AT, DELETEYN,
  SI_NO, GU_NO, THEME_NO, DESCRIPTION, VEHICLE
)
VALUES (
  #{guideNo}, #{title}, #{duration}, #{price}, 4,
  #{tourDate}, NOW(), NOW(), 'N',
  #{siNo}, #{guNo}, #{themeNo}, #{description}, #{vehicle}
)

mapper.select:
SELECT COUNT(DISTINCT T.TOUR_NO) FROM TOURS T INNER JOIN THEME M ON T.THEME_NO = M.THEME_NO LEFT JOIN ( SELECT TOUR_NO, ROUND(AVG(RATING),1) AS RATING FROM REVIEWS GROUP BY TOUR_NO ) TEMP1 ON T.TOUR_NO = TEMP1.TOUR_NO LEFT JOIN IMG I ON T.TOUR_NO = I.TOUR_NO AND THUMBNAIL = 'Y' INNER JOIN ( SELECT DISTINCT SI_NO, SI_NAME FROM REGION ) R ON T.SI_NO = R.SI_NO INNER JOIN GUIDES G ON T.GUIDE_NO = G.GUIDE_NO WHERE T.DELETEYN = 'N'
AND T.SI_NO = #{siNo}
AND T.SI_NO IN (34, 37, 26, 38, 25, 22, 29, 24, 33, 36)
AND T.TITLE LIKE CONCAT('%', #{keyword}, '%')
AND TOUR_DATE BETWEEN
#{item}
AND T.THEME_NO IN
#{item}
AND T.SI_NO IN
#{item}
AND (
LANGUAGE LIKE CONCAT('%', #{item}, '%')
)
